import React, { useEffect, useState } from "react";
import './App.css';
import Card from "./Card";

const App = () => {
    const API_URL = `http://aleksanderblaszkiewicz.pl/callendar/get_events.php`;
    const [items, setItems] = useState([]);

    useEffect(() => {
        getItems();
    }, []);

    const getEvents = () => {
        fetch(API_URL)
        .then((response) => response.json())
        .then((data) => console.log(data))
        .then((data) => setEvents(data));
    }

    return  (
        <>
            <div className="App">
                <h1>Kalendarz</h1>
                {items.map(item => (
                    <Card />
                ))};
            </div>
        </>
    )
}

export default App;
