import React, { useEffect, useState } from "react";
import $ from "react-ajax";

const AddEventPanel = () => {
    const API_URL = `http://aleksanderblaszkiewicz.pl/kiedykolos/get_courses.php`;
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        getCourses();
    }, []);

    const getCourses = async () => {
        const response = await fetch(API_URL);
        const data = await response.json();
        setCourses(data);
        console.log(courses);
    }

