    return(
        <>
            <h1>Przedmioty: </h1>

            <select name="course" id="course" form="add-event">
            {courses.map(course => (
                <option value={course.id}>{course.name}</option>
            ))}
            </select>
            <input type="date" id="date" name="date" value="2020-11-25" min="2020-11-25" max="2021-12-31"></input>
            
            {/* <button onClick={this.onFormSubmitSuccess.bind(this)} >click here</button> */}
            
        </>
    )
}

export default AddEventPanel;
