import React, { useEffect, useState } from "react";
import './../CallendarCard.css';

const CallendarCard = ({day, allEvents}) => {
    const [dayEvents, setDayEvents] = useState([]);

    useEffect(() => {
        initializeDayEvents();
    }, [allEvents]);

    const initializeDayEvents = () => {
        let events = [];
        allEvents.forEach(event => {
            let dateDay = event.date.slice(event.date.length-2,event.date.length);
            
            if(dateDay == day) {
                events.push(event);
            }
        });
        setDayEvents(events);
        console.log(events);
    }


    return(
        <div class="callendar-card">
            <p>dzien: {day} <br></br>
            {dayEvents.map(event => (
                <>{event.name}</>
            ))}
            </p>
        </div>
    )
}

export default CallendarCard;
