import React, { useEffect, useState } from "react";

const AddEventPanel = () => {
    const API_URL_GET = `http://aleksanderblaszkiewicz.pl/kiedykolos/get_courses.php`;
    const [courses, setCourses] = useState([]);
    const [name, setName] = useState("jezyki programowania");

    useEffect(() => {
        getCourses();
    }, []);

    const getCourses = async () => {
        const response = await fetch(API_URL_GET);
        const data = await response.json();
        setCourses(data);
        console.log(courses);
    }

    const addEvent = () => {
        fetch('http://aleksanderblaszkiewicz.pl/kiedykolos/get_courses.php?name=', { // URL
            body: JSON.stringify(this.state), // data you send.
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            headers: {
              'content-type': 'application/json'
            },
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, cors, *same-origin
            redirect: 'follow', // *manual, follow, error
            referrer: 'no-referrer', // *client, no-referrer
        })
        .then(function(response) {
            // manipulate response object
            // check status @ response.status etc.
            return response.json(); // parses json
        })
        .then(function(myJson) {
            // use parseed result
            console.log(myJson);
        });
    }

    return(
        <>
            <h1>Przedmioty: </h1>

            <select name="course" id="course" form="add-event">
            {courses.map(course => (
                <option value={course.id}>{course.name}</option>
            ))}
            </select>
            <input type="date" id="date" name="date" value="2020-11-25" min="2020-11-25" max="2021-12-31"></input>
            
            <button onClick={addEvent} >click here</button>
            
        </>
    )
}

export default AddEventPanel;
