import React, { useEffect, useState } from "react";
import CallendarCard from "./CallendarCard";
import AddEventPanel from "./AddEventPanel";

const Callendar = () => {
    const API_URL = `http://aleksanderblaszkiewicz.pl/kiedykolos/get_events.php`;
    const [events, setEvents] = useState([]);
    
    let days = new Array(31);

    useEffect(() => {
        getEvents().then(initializeDays());
        
    }, []);

    const getEvents = async () => {
        const response = await fetch(API_URL);
        const data = await response.json();
        setEvents(data);
    }

    const initializeDays = () => {
        for(let i=0; i<31; i++) {
            events.forEach(event => {
                let day = event.date.slice(event.date.length-2,event.date.length);
                if(day == i) days[i] = event;
            });
        }
    }

    return (
        <div className="Callendar">
            <AddEventPanel refreshEvents={getEvents}/>
            {days.map(item => (
                <CallendarCard
                    name={item.id}
                    date="date"
                    />
            ))}
        </div>
    )

}

export default Callendar;
