import React, { useEffect, useState } from "react";
import './../App.css';



const InfoPanel = () => {
    const [events, setEvents] = useState([]);
    const [date, setDate] = useState("2020-12-01");

    const API_URL = `http://aleksanderblaszkiewicz.pl/kiedykolos/get_events_specific.php?date=${date}`;

    useEffect(() => {
        getEvents();
    }, []);

    const getEvents = async () => {
        const response = await fetch(API_URL);
        const data = await response.json();
        setEvents(data);
        console.log("Events for date: " + date);
        console.log(events);
    }

    return(
        <div className="info-panel">
            <p>Wybrana data: {date}</p>
            {events.map((event) => (
                <InfoEvent key={event.name} title={event.name}/>
            ))}
            
        </div>
    )
}

const InfoEvent = ({title}) => {
    return (
        <div className="info-event">
            <p>{title}</p>
        </div>
    )
}

export default InfoPanel;
